import React from 'react';
import './FAQs.css';

const faqs = [
    {
        question: "What is ClasherStation?",
        answer: "ClasherStation is your one-stop destination for the ultimate gaming experience. We offer a wide range of high-quality gaming consoles, games, and accessories."
    },
    {
        question: "How can I create an account?",
        answer: "To create an account, click on the 'Account' link at the top of the page, then select 'Register' and fill out the required information."
    },
    {
        question: "What payment methods do you accept?",
        answer: "We accept various payment methods including credit cards, debit cards, PayPal, and other secure payment options."
    },
    {
        question: "How do I track my order?",
        answer: "Once your order is shipped, you will receive a tracking number via email. You can use this number to track your order on our website."
    },
    {
        question: "Can I change or cancel my order?",
        answer: "If you need to change or cancel your order, please contact our customer service team as soon as possible. Changes or cancellations may not be possible if the order has already been processed."
    },
    {
        question: "What is your return policy?",
        answer: "We offer a 30-day return policy on most items. Please visit our Returns page for more details."
    },
    {
        question: "Do you offer international shipping?",
        answer: "Yes, we offer international shipping to many countries. Shipping fees and delivery times vary depending on the destination."
    },
    {
        question: "How can I contact customer support?",
        answer: "You can contact our customer support team via email at support@clasherstation.com or by calling +91 9608632246."
    },
    {
        question: "Are your products covered by a warranty?",
        answer: "Yes, most of our products come with a manufacturer's warranty. Please refer to the product details for specific warranty information."
    },
    {
        question: "How do I apply a discount code?",
        answer: "You can apply a discount code at checkout. Enter the code in the designated field and click 'Apply' to see the discount reflected in your total."
    },
    {
        question: "What should I do if I receive a damaged or defective item?",
        answer: "If you receive a damaged or defective item, please contact our customer service team immediately to arrange a return or exchange."
    },
    {
        question: "How do I sign up for the newsletter?",
        answer: "You can sign up for our newsletter by entering your email address in the newsletter subscription box located at the bottom of our homepage."
    },
    {
        question: "Can I change my shipping address after placing an order?",
        answer: "If you need to change your shipping address, please contact our customer service team as soon as possible. Changes may not be possible if the order has already been processed."
    },
    {
        question: "What are your shipping rates?",
        answer: "Shipping rates vary depending on the destination and shipping method selected. Shipping costs will be calculated at checkout."
    },
    {
        question: "How do I know if an item is in stock?",
        answer: "The availability of an item is displayed on the product page. If an item is out of stock, it will be indicated on the page."
    },
    {
        question: "Can I pre-order upcoming products?",
        answer: "Yes, we offer pre-orders for select upcoming products. Pre-order availability will be indicated on the product page."
    },
    {
        question: "What do I do if I forget my password?",
        answer: "If you forget your password, click on the 'Forgot Password' link on the login page and follow the instructions to reset your password."
    },
    {
        question: "How can I leave a review for a product?",
        answer: "You can leave a review for a product by going to the product page and clicking on the 'Write a Review' button."
    },
    {
        question: "Do you have a physical store?",
        answer: "ClasherStation is an online-only store. We do not have any physical retail locations."
    },
    {
        question: "Can I request a product that is not listed on your website?",
        answer: "If you are looking for a specific product that is not listed on our website, please contact our customer service team and we will do our best to assist you."
    }
];

const FAQs = () => {
    return (
        <section className="faqs">
            <h2>Frequently Asked Questions</h2>
            <div className="faq-list">
                {faqs.map((faq, index) => (
                    <div className="faq-item" key={index}>
                        <h3>{faq.question}</h3>
                        <p>{faq.answer}</p>
                    </div>
                ))}
            </div>
        </section>
    );
};

export default FAQs;
