import React, { useState } from 'react';
import './Hero.css';

const videos = [
    '/videos/video1.mp4',
    '/videos/video2.mp4',
    '/videos/video3.mp4',
    '/videos/video4.mp4',
    '/videos/video5.mp4'
];

const Hero = () => {
    const [currentVideoIndex, setCurrentVideoIndex] = useState(0);

    const nextVideo = () => {
        setCurrentVideoIndex((prevIndex) => (prevIndex + 1) % videos.length);
    };

    const prevVideo = () => {
        setCurrentVideoIndex((prevIndex) => (prevIndex - 1 + videos.length) % videos.length);
    };

    return (
        <section className="hero">
            <video autoPlay muted loop className="hero-video">
                <source src={videos[currentVideoIndex]} type="video/mp4" />
                Your browser does not support the video tag.
            </video>
            <div className="hero-text">
                <h1>Welcome to ClasherStation</h1>
                <p>Experience the ultimate gaming destination</p>
                <a href="#" className="cta">Shop Now</a>
            </div>
            <button className="prev-button" onClick={prevVideo}>❮</button>
            <button className="next-button" onClick={nextVideo}>❯</button>
        </section>
    );
};

export default Hero;
