import React, { useState } from 'react';
import './Hero.css';

const images = [
    '/images/image1.jpg',
    '/images/image2.jpg',
    '/images/image3.jpg',
    '/images/image4.jpg',
    '/images/image5.jpg'
];

const Hero = () => {
    const [currentImageIndex, setCurrentImageIndex] = useState(0);

    const nextImage = () => {
        setCurrentImageIndex((prevIndex) => (prevIndex + 1) % images.length);
    };

    const prevImage = () => {
        setCurrentImageIndex((prevIndex) => (prevIndex - 1 + images.length) % images.length);
    };

    return (
        <section className="hero">
            <img src={images[currentImageIndex]} alt="Hero Image" className="hero-image" />
            <div className="hero-text">
                <h1>Welcome to ClasherStation</h1>
                <p>Experience the ultimate gaming destination</p>
                <a href="#" className="cta">Shop Now</a>
            </div>
            <button className="prev-button" onClick={prevImage}>❮</button>
            <button className="next-button" onClick={nextImage}>❯</button>
        </section>
    );
};

export default Hero;
